--
-- Name: adm_create_spot_market(character varying, character varying); Type: FUNCTION; Schema: ex; Owner: -
--

CREATE FUNCTION ex.adm_create_spot_market(p_base_asset character varying, p_quote_asset character varying) RETURNS ex.markets_available
    LANGUAGE sql SECURITY DEFINER
    SET search_path TO 'ex', 'p2p'
    AS $$
with pair as (
    select p.name, b.code base_code, q.code quote_code
    from (values (p_base_asset || '-' || p_quote_asset)) p(name)
             left join crypto_asset b on b.code = p_base_asset
             left join crypto_asset q on q.code = p_quote_asset
    where not exists(select from markets_available where name = p.name)
)
insert
into markets_available(name, label, base_asset, quote_asset)
select pair.name, pair.name, pair.base_code, pair.quote_code
from pair
         cross join lateral
    (values (createPartitionForPair(name, 'ohlcv'),
             createPartitionForPair(name, 'order_book'),
             createPartitionForPair(name, 'settlement_queue'),
             createPartitionForPair(name, 'conditional_orders_book'))
    ) cp
returning *
$$;


