--
-- Name: rep_recent_trades_agg(integer, integer, integer, bigint, character varying); Type: FUNCTION; Schema: ex; Owner: -
--

CREATE FUNCTION ex.rep_recent_trades_agg(p_user_id integer, p_past_days integer DEFAULT 7, p_limit integer DEFAULT 40, p_order_id bigint DEFAULT NULL::bigint, p_pair character varying DEFAULT NULL::character varying) RETURNS TABLE(id bigint, pair character varying, side ex.order_side, last_trade_at timestamp without time zone, matched_unit_price numeric, matched_base_amount numeric, matched_quote_amount numeric, fee numeric)
    LANGUAGE sql STABLE SECURITY DEFINER PARALLEL SAFE
    SET search_path TO 'ex'
    AS $$
select o.id,
       o.pair,
       o.side,
       last_trade_at,
       matched_unit_price,
       matched_base_amount,
       matched_quote_amount,
       fee
from (
         SELECT oeh.order_id
         FROM orders_exec_hist oeh
         where oeh.status IN ('partial', 'complete')
           AND DATE_TRUNC('day', oeh.at) >= (CURRENT_DATE - make_interval(days => p_past_days))
           AND oeh.user_id = p_user_id
           and (p_order_id is null or oeh.order_id = p_order_id)
           and (p_pair is null or oeh.pair = p_pair)
         GROUP BY oeh.order_id
         ORDER BY max(oeh.trade_id) DESC
         LIMIT p_limit
     ) ord
         join orders o on o.id = ord.order_id
         cross join lateral (
    select th.matched_unit_price,
           max(oehl.at)         last_trade_at,
           sum(th.base_amount)  matched_base_amount,
           sum(th.quote_amount) matched_quote_amount,
           sum(oehl.fee)        fee
    from trades_hist th
             join orders_exec_hist oehl on th.id = oehl.trade_id
    where oehl.order_id = ord.order_id
    group by th.matched_unit_price
    ) l
order by last_trade_at desc
limit p_limit
$$;


