--
-- Name: update_ohlcv(character varying, timestamp without time zone, numeric, numeric, numeric, numeric, numeric, numeric, bigint); Type: FUNCTION; Schema: ex; Owner: -
--

CREATE FUNCTION ex.update_ohlcv(p_pair character varying, p_start_date timestamp without time zone, p_open_price numeric, p_close_price numeric, p_low_price numeric, p_high_price numeric, p_base_volume numeric, p_quote_volume numeric, p_trades_count bigint) RETURNS SETOF ex.ohlcv
    LANGUAGE sql
    SET search_path TO 'ex'
    AS $$
insert into ohlcv(width,
                  pair, start_date,
                  open_price, close_price,
                  low_price, high_price,
                  base_volume, quote_volume,
                  trades_count)
select iter.width,
       p_pair,
       date_trunc(iter.width::text, p_start_date) start_date,
       p_open_price,
       p_close_price,
       p_low_price,
       p_high_price,
       p_base_volume,
       p_quote_volume,
       p_trades_count
from (SELECT unnest(enum_range(NULL::ohlcv_width)) width) iter
on conflict(width, pair, start_date)
    do update
    SET close_price  = excluded.close_price,
        low_price    = least(ohlcv.low_price, excluded.low_price),
        high_price   = greatest(ohlcv.high_price, excluded.high_price),
        base_volume  = ohlcv.base_volume + excluded.base_volume,
        quote_volume = ohlcv.quote_volume + excluded.quote_volume,
        trades_count = ohlcv.trades_count + excluded.trades_count
returning *;
$$;


