    v_partition_name text := p_table || '$' || to_char(p_date, 'YYYYMMDD');
begin
    -- If partition exists...:
    if to_regclass(v_partition_name) is not null then
        -- Drop old partition
        execute format('drop table %I', v_partition_name);
