    LANGUAGE sql
    SET search_path TO 'public'
    AS $$
select partition_name::text, SUBSTRING(expr, 'FOR VALUES IN \((.*)\)')::date for_date
from (
         select pt.relname as                              partition_name,
                pg_get_expr(pt.relpartbound, pt.oid, true) expr
         from pg_class base_tb
