-- Name: order_book; Type: TABLE; Schema: ex; Owner: -
--

CREATE TABLE ex.order_book (
    order_id bigint NOT NULL,
    user_id integer NOT NULL,
    pair character varying(20) NOT NULL,
    side ex.order_side NOT NULL,
    type ex.order_type NOT NULL,
    unit_price numeric,
    amount_type ex.amount_type DEFAULT 'base'::ex.amount_type NOT NULL,
    available_amount numeric NOT NULL,
    balance_on_hold numeric NOT NULL,
    in_depth boolean DEFAULT false NOT NULL,
    CONSTRAINT order_book_available_amount_check CHECK ((available_amount > (0)::numeric)),
    CONSTRAINT order_book_balance_on_hold_check CHECK ((balance_on_hold > (0)::numeric)),
    CONSTRAINT order_book_check CHECK (((amount_type = 'base'::ex.amount_type) OR (type = 'market'::ex.order_type))),
    CONSTRAINT order_book_check1 CHECK ((NOT (in_depth AND (type = 'market'::ex.order_type)))),
    CONSTRAINT order_book_check2 CHECK ((((type = 'limit'::ex.order_type) AND (unit_price IS NOT NULL) AND (unit_price > (0)::numeric)) OR ((type = 'market'::ex.order_type) AND (unit_price IS NULL))))
)
PARTITION BY LIST (pair);


--
-- Name: mon$order_book_no_liquidity; Type: VIEW; Schema: ex; Owner: -
--

CREATE VIEW ex."mon$order_book_no_liquidity" AS
 SELECT DISTINCT ma.name AS pair
   FROM ((ex.markets_available ma
     CROSS JOIN ( VALUES ('buy'::text), ('sell'::text)) side(side))
     LEFT JOIN ex.order_book ob ON ((((ob.pair)::text = (ma.name)::text) AND (ob.side = (side.side)::ex.order_side) AND ob.in_depth)))
  WHERE ma.is_active
  GROUP BY ma.name, ob.side
 HAVING (count(ob.order_id) = 0)
  ORDER BY ma.name;


--
-- Name: mon$order_book_size; Type: VIEW; Schema: ex; Owner: -
--

CREATE VIEW ex."mon$order_book_size" AS
 SELECT ma.name AS pair,
    count(ob.order_id) AS size
   FROM (ex.markets_available ma
     LEFT JOIN ex.order_book ob ON ((((ob.pair)::text = (ma.name)::text) AND ob.in_depth)))
  WHERE ma.is_active
  GROUP BY ma.name
  ORDER BY ma.name;


