    trigger_price ex.positive_monetary,
    trigger_price_trailing ex.trailing_predicate,
    limit_price_trailing ex.trailing_predicate,
    CONSTRAINT orders_amount_check CHECK ((amount > (0)::numeric)),
    CONSTRAINT orders_check CHECK (((amount_type = 'base'::ex.amount_type) OR (type = 'market'::ex.order_type))),
    CONSTRAINT orders_check1 CHECK ((((type = 'limit'::ex.order_type) AND (unit_price > (0)::numeric)) OR ((type = 'market'::ex.order_type) AND (unit_price IS NULL)))),
    CONSTRAINT orders_check2 CHECK (((NOT is_oco) OR ((side = 'sell'::ex.order_side) = (oco_buy_id IS NULL)))),
    CONSTRAINT orders_check3 CHECK ((NOT (is_oco AND (trigger_objective IS NULL))))
)
PARTITION BY RANGE (id);


--
-- Name: mon$order_book_stale; Type: VIEW; Schema: ex; Owner: -
--

CREATE VIEW ex."mon$order_book_stale" AS
 SELECT ma.name AS pair,
    date_part('epoch'::text, (LOCALTIMESTAMP - o.created_at)) AS latency
   FROM (ex.markets_available ma
     LEFT JOIN LATERAL ( SELECT o_1.created_at
           FROM ex.orders o_1
          WHERE (o_1.id = ( SELECT min(ob.order_id) AS min_id
                   FROM ex.order_book ob
