--
-- Name: mon$settlement_queue_stale; Type: VIEW; Schema: ex; Owner: -
--

CREATE VIEW ex."mon$settlement_queue_stale" AS
 SELECT ma.name AS pair,
    date_part('epoch'::text, (LOCALTIMESTAMP - t.at)) AS latency
   FROM (ex.markets_available ma
     LEFT JOIN LATERAL ( SELECT th.at
           FROM ex.trades_hist th
