     LEFT JOIN ex.ohlcv ON ((((ma.name)::text = (ohlcv.pair)::text) AND (ohlcv.width = 'minute'::ex.ohlcv_width) AND (ohlcv.start_date = (date_trunc('minute'::text, now()) - '00:01:00'::interval)))))
