--
-- Name: vw_market_pairs; Type: VIEW; Schema: ex; Owner: -
--

CREATE VIEW ex.vw_market_pairs AS
 SELECT t.low_price,
    t.high_price,
    t.open_price,
    t.close_price,
    t.base_volume,
    t.quote_volume,
    taker.taker_side,
    ma.name,
    ma.label,
    ma.taker_fee,
    ma.maker_fee,
    base_cur.min_order_amount AS min_order_base_amount,
    quote_cur.min_order_amount AS min_order_quote_amount,
    base_cur.code AS base_currency,
    quote_cur.code AS quote_currency,
    (r.value * t.close_price) AS usd_close_price
   FROM (((((ex.markets_available ma
     JOIN ex.crypto_asset base_cur ON (((base_cur.code)::text = (ma.base_asset)::text)))
     JOIN ex.crypto_asset quote_cur ON (((quote_cur.code)::text = (ma.quote_asset)::text)))
     LEFT JOIN p2p.rate r ON ((((r.cc_code)::text = (quote_cur.code)::text) AND ((r.currency_symbol)::text = 'USD'::text) AND r.default_rate)))
     LEFT JOIN LATERAL ( SELECT trades_hist.taker_side
           FROM ex.trades_hist
          WHERE ((trades_hist.pair)::text = (ma.name)::text)
          ORDER BY trades_hist.id DESC
         LIMIT 1) taker ON (true))
     LEFT JOIN LATERAL ( SELECT min(t_1.low_price) AS low_price,
            max(t_1.high_price) AS high_price,
            sum(t_1.base_volume) AS base_volume,
            sum(t_1.quote_volume) AS quote_volume,
            t_1.open_price,
            t_1.close_price
           FROM ( SELECT c.low_price,
                    c.high_price,
                    c.base_volume,
                    c.quote_volume,
                    first_value(c.open_price) OVER win AS open_price,
                    last_value(c.close_price) OVER win AS close_price
                   FROM ex.ohlcv c
                  WHERE ((c.width = 'minute'::ex.ohlcv_width) AND (c.start_date >= (now() - '1 day'::interval)) AND ((c.pair)::text = (ma.name)::text))
                  WINDOW win AS (ORDER BY c.start_date ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING)) t_1
          GROUP BY t_1.open_price, t_1.close_price) t ON (true))
  WHERE ma.is_active
  ORDER BY ( SELECT cc.weight
           FROM public.cryptocurrency cc
          WHERE ((cc.code)::text = (quote_cur.code)::text)), ( SELECT cc.weight
           FROM public.cryptocurrency cc
          WHERE ((cc.code)::text = (base_cur.code)::text));


--
-- Name: vw_order_exec_info; Type: VIEW; Schema: ex; Owner: -
--

CREATE VIEW ex.vw_order_exec_info AS
 SELECT o.id,
    o.user_id,
    o.pair,
    o.side,
    o.type,
    o.amount_type,
    o.amount,
    o.unit_price,
    o.created_at,
    o.is_oco,
    o.oco_buy_id,
    o.trigger_objective,
    o.trigger_price,
    o.trigger_price_trailing,
    o.limit_price_trailing,
    t.pending_amount,
    t.balance_on_hold,
    t.child_order_id,
    t.updated_at,
    t.total_fee,
    t.last_status,
    t.matched_base_amount,
    t.matched_quote_amount
   FROM ex.orders o,
    LATERAL ( SELECT t_1.pending_amount,
            t_1.balance_on_hold,
            t_1.child_order_id,
            max(t_1.updated_at) AS updated_at,
            sum(t_1.fee) AS total_fee,
            max(t_1.status) AS last_status,
            sum(t_1.base_amount) AS matched_base_amount,
            sum(t_1.quote_amount) AS matched_quote_amount
           FROM ( SELECT eh.at AS updated_at,
                    first_value(eh.pending_amount) OVER w AS pending_amount,
                    first_value(eh.balance_on_hold) OVER w AS balance_on_hold,
                    eh.fee,
                    eh.status,
                    th.base_amount,
                    th.quote_amount,
                    first_value(eh.child_order_id) OVER w AS child_order_id
                   FROM (ex.orders_exec_hist eh
                     LEFT JOIN ex.trades_hist th ON ((th.id = eh.trade_id)))
                  WHERE (o.id = eh.order_id)
                  WINDOW w AS (PARTITION BY o.id ORDER BY eh.status DESC, eh.trade_id DESC)) t_1
          GROUP BY t_1.pending_amount, t_1.balance_on_hold, t_1.child_order_id) t;


